import React, { useState } from 'react';

const LoginPage = () => {
  const banks = {
    'Bank of India': '1234567890',
    'SecureBank': '9876543210',
    'State Bank': '1122334455',
  };

  const [selectedBank, setSelectedBank] = useState('');
  const [accountNumber, setAccountNumber] = useState('');
  const [password, setPassword] = useState('');

  const handleBankChange = (e) => {
    const bank = e.target.value;
    setSelectedBank(bank);
    setAccountNumber(banks[bank] || '');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Logged in with:\nBank: ${selectedBank}\nAccount: ${accountNumber}`);
  };

  return (
    <div className="min-h-screen bg-blue-800 flex items-center justify-center">
      <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4 text-center">Bank Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Select Bank</label>
            <select
              className="w-full border rounded px-3 py-2"
              value={selectedBank}
              onChange={handleBankChange}
            >
              <option value="">-- Choose Bank --</option>
              {Object.keys(banks).map((bank) => (
                <option key={bank} value={bank}>
                  {bank}
                </option>
              ))}
            </select>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Account Number</label>
            <input
              type="text"
              className="w-full border rounded px-3 py-2"
              value={accountNumber}
              onChange={(e) => setAccountNumber(e.target.value)}
              placeholder="Enter your account number"
            />
          </div>

          <div className="mb-6">
            <label className="block text-sm font-medium mb-1">Password</label>
            <input
              type="password"
              className="w-full border rounded px-3 py-2"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
          >
            Sign In
          </button>
        </form>

        <p className="text-xs text-center mt-4 text-gray-500">
          Demo: Select bank and use password like "password123"
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
